    <script language="javascript" type="text/javascript"> 
 
        function validateEmailID(emailID) { if (emailID != "") { var reg = /^([A-Za-z0-9_\-\.])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,4})$/; if (reg.test(emailID) == false) { return false; } } return true; }
        $(document).ready(function () {
            $('#btnHomeEnquiry').click(function () {      
                var nameD = $('#nameH').val();
                var mobileD = $('#mobileH').val();
                var emailD = $('#emailH').val();
                var dotD = $('#dotH').val();
                var adultsD = $('#adultsH').val();
                var kidsD = $('#kidsH').val(); 
                var categoryD = $('#categoryH').val();  
                var durationD = $('#durationH').val();             
                var messageD = $('#messageH').val();
                var sourceD = $('#sourceH').val();
                var disposition='new query';
                var ipD = $('#ipH').val();
                var validate = true;
                var countryH = $('#countryH').val(); 

                $('#nameD').css('border-color', '#ccc');
                $('#mobileD').css('border-color', '#ccc');                
                $('#emailD').css('border-color', '#ccc');
                $('#dotD').css('border-color', '#ccc');
                $('#adultsD').css('border-color', '#ccc');
                $('#durationD').css('border-color', '#ccc');
                $('#messageD').css('border-color', '#ccc');

                if (nameD == '') {
                    $('#nameD').css('border-color', 'red');
                    validate = false;
                }
                 
                else if (emailD == '' || !validateEmailID(emailD)) {
                    $('#emailD').css('border-color', 'red');
                    validate = false;
                }
                else if (mobileD == '') {
                    $('#mobileD').css('border-color', 'red');
                    validate = false;
                }
                else if (dotD == '') {
                    $('#dotD').css('border-color', 'red');
                    validate = false;
                }
                else if (adultsD == '') {
                    $('#adultsD').css('border-color', 'red');
                    validate = false;
                }  
                else if (kidsD == '') {
                    $('#kidsD').css('border-color', 'red');
                    validate = false;
                } 
                else if (durationD == '') {
                    $('#durationD').css('border-color', 'red');
                    validate = false;
                }  
                else if (messageD == '') {
                    $('#messageD').css('border-color', 'red');
                    validate = false;
                }           
                if (validate) { 
                    $('#btnHomeEnquiry').attr('value', 'Please Wait...');
                    $('#btnHomeEnquiry').attr('disabled', true);
                   var URL ='https://dialcrm.com/WebApi/pushCRMTicket/5862A139AeGQdBr?name='+nameD+'&countrycode=91&mobile='+mobileD+'&altmobiles='+mobileD+'&emailid='+emailD+'&altemailids='+emailD+'&remarks='+messageD+'&disposition='+disposition+'&agentmobile=agentmobile&leadsource='+sourceD+'&apiCrmDataJson=[{"name":"Your City","value":"'+ipD+'"},{"name":"Date of Travel","value":"'+dotD+'"},{"name":"Trip Planning Stage","value":"'+countryH+'"},{"name":"Adult","value":"'+adultsD+'"},{"name":"Kids","value":"'+kidsD+'"},{"name":"Kid Age 1","value":""},{"name":"Kid Age 2","value":""},{"name":"Kid Age 3","value":""},{"name":"Kid Age 4","value":""},{"name":"URL","value":"GameOfTravels Popup Form"},{"name":"Hotel Category","value":"'+categoryD+'"},{"name":"Tour Duration","value":"'+durationD+'"},{"name":"Additional Info","value":"'+messageD+'"},{"name":"Lead Source","value":"'+sourceD+'"},{"name":"Destination","value":""}]';
                   
                   
                    
                   
                   
                   
                   
                    var xmlhttp;
                    if (window.XMLHttpRequest) {// code for IE7+, Firefox, Chrome, Opera, Safari
                        xmlhttp = new XMLHttpRequest();
                    }
                    else {// code for IE6, IE5
                        xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");
                    }
                    xmlhttp.open("GET", URL, true);
                    xmlhttp.send();
                    xmlhttp.onreadystatechange = function () {
                        if (xmlhttp.readyState == 4 && xmlhttp.status == 200) {
                            $('#btnHomeEnquiry').attr('value', 'Submit');
                            $('#btnHomeEnquiry').removeAttr("disabled");
                            //resetValues();
                            //alert("Your Query Submited Successfully!");
                            window.location.href = "/thankyou.php";
                        }
                    }
                }
            });
        });
        
    </script>
